import Ember from 'ember';

const { assert } = Ember;
const { floor } = Math;

export default function checkScrollDirection(lastPosition = null, newPosition = {}, sensitivity = 1) {
  if (!lastPosition) {
    return 'none';
  }

  assert('sensitivity cannot be 0', sensitivity);

  const { top, left } = newPosition;
  const { top: lastTop, left: lastLeft } = lastPosition;

  const delta = {
    top: floor((top  - lastTop)  / sensitivity) * sensitivity,
    left: floor((left - lastLeft) / sensitivity) * sensitivity
  };

  if (delta.top > 0) {
    return 'down';
  }

  if (delta.top < 0) {
    return 'up';
  }

  if (delta.left > 0) {
    return 'right';
  }

  if (delta.left < 0) {
    return 'left';
  }
}

import Ember from 'ember';

const assign = Ember.assign || Ember.merge;

const defaultTolerance = {
  top: 0,
  left: 0,
  bottom: 0,
  right: 0
};

export default function isInViewport(boundingClientRect = {}, height = 0, width = 0, tolerance = defaultTolerance) {
  const { top, left, bottom, right } = boundingClientRect;
  const tolerances = assign(assign({}, defaultTolerance), tolerance);
  const {
    top: topTolerance,
    left: leftTolerance,
    bottom: bottomTolerance,
    right: rightTolerance
  } = tolerances;

  return (
    (top + topTolerance)       >= 0 &&
    (left + leftTolerance)     >= 0 &&
    (Math.round(bottom) - bottomTolerance) <= Math.round(height) &&
    (Math.round(right) - rightTolerance)   <= Math.round(width)
  );
}

import Ember from 'ember';
import config from '../config/environment';
import canUseDOM from 'ember-in-viewport/utils/can-use-dom';

const defaultConfig = {
  viewportEnabled: true,
  viewportSpy: false,
  viewportScrollSensitivity: 1,
  viewportRefreshRate: 100,
  viewportListeners: [
    { context: window, event: 'scroll.scrollable' },
    { context: window, event: 'resize.resizable' }
  ],
  viewportTolerance: {
    top: 0,
    left: 0,
    bottom: 0,
    right: 0
  }
};

if (canUseDOM) {
  defaultConfig.viewportListeners.push({
    context: document,
    event: 'touchmove.scrollable'
  });
}

const assign = Ember.assign || Ember.merge;

export function initialize() {
  const application = arguments[1] || arguments[0];
  const { viewportConfig = {} } = config;
  const mergedConfig = assign({}, defaultConfig, viewportConfig);

  application.register('config:in-viewport', mergedConfig, { instantiate: false });
}

export default {
  name: 'viewport-config',
  initialize: initialize
};
